import { Streamlit, RenderData } from "streamlit-component-lib"
import * as d3 from 'd3'

interface AxisLabel {
    x: number
    label: string
    labelsize: number
}

interface ClusterLink {
    x: number[]
    y: number[]
    fillcolor: string
    size: number
}

interface ClusterNode {
    x: number
    y: number
    edgecolor: string
    fillcolor: string
    label: string
    hovertext: Object[] | string 
    size: number
    labelsize: number
    labelcolor: string

}

interface Dendrogram {
    axis_labels: AxisLabel[]
    links: ClusterLink[]
    nodes: ClusterNode[]
}

function create_container(width=960, height=500) {
    let margin = {top: 20, right: 10, bottom: 20, left: 10},
    outerWidth = width,
    outerHeight = height,
    innerWidth = outerWidth - margin.left - margin.right,
    innerHeight = outerHeight - margin.top - margin.bottom

    // append svg element to the body of the page
    // set dimensions and position of the svg element
    let svg = d3
        .select("body")
        .append("svg")
        .attr("id", "idendro")
        .attr("width", outerWidth)
        .attr("height", outerHeight)

    let plot = svg.append("g")
        .attr("transform", "translate(" + margin.left + "," + margin.top + ")")
        .attr("id", "idendro-container");

    return {'svg': svg, 'plot': plot, 'innerWidth': innerWidth, 'innerHeight': innerHeight}
}



let isFocused = false

