import { Streamlit, RenderData } from "streamlit-component-lib"
import * as d3 from 'd3'

interface AxisLabel {
    x: number
    label: string
    labelsize: number
}

interface ClusterLink {
    x: number[]
    y: number[]
    fillcolor: string
    size: number
}

interface ClusterNode {
    x: number
    y: number
    edgecolor: string
    fillcolor: string
    label: string
    hovertext: Object[] | string
    size: number
    labelsize: number
    labelcolor: string

}

interface Dendrogram {
    axis_labels: AxisLabel[]
    links: ClusterLink[]
    nodes: ClusterNode[]
    x_limits: [number, number]
    y_limits: [number, number]
}

interface Dimensions {
    margin: Margin
    height: number
    width: number
    innerHeight: number
    innerWidth: number,
    orientation: Orientation
}

enum Orientation {
    top = "top",
    bottom = "bottom",
    right = "right",
    left = "left"
}

interface Margin {
    top: number
    right: number
    bottom: number
    left: number
}

interface plot extends d3.Selection<SVGGElement, unknown, HTMLElement, any> {

}

function create_container(dimensions: Dimensions): plot {


    if (d3.select("#idendro")) {
        d3.select("#idendro").remove()
    }
    // append svg element to the body of the page
    // set dimensions and position of the svg element
    let svg = d3
        .select("body")
        .append("svg")
        .attr("id", "idendro")
        .attr("width", dimensions.width)
        .attr("height", dimensions.height)

    let plot = svg.append("g")
        .attr("transform", "translate(" + dimensions.margin.left + "," + dimensions.margin.top + ")")
        .attr("id", "idendro-container");

    return plot
}

function create_axis(plot: plot, dimensions: Dimensions, dendrogram: Dendrogram) {

    let width_limits, height_limits, height_range, width_range = [0, 0]
    let x_axis_func, y_axis_func : CallableFunction
    let x_axis_transform =  dimensions.innerHeight
    let y_axis_transform = 0

    //handle orientation impact on scale ranges & positioning
    if (dimensions.orientation === Orientation.top || dimensions.orientation === Orientation.bottom) {
        height_limits = dendrogram.y_limits
        width_limits = dendrogram.x_limits
        width_range = [0, dimensions.innerWidth]
        y_axis_func = d3.axisLeft
        if (dimensions.orientation === Orientation.top) {
            height_range = [dimensions.innerHeight, 0]
            x_axis_func = d3.axisBottom
        } else {
            height_range = [0, dimensions.innerHeight]
            x_axis_func = d3.axisTop
            x_axis_transform = 0
        }        
    } else {
        x_axis_func = d3.axisBottom
        height_limits = dendrogram.x_limits
        width_limits = dendrogram.y_limits                
        height_range = [dimensions.innerHeight, 0]
        if (dimensions.orientation === Orientation.left) {
            width_range = [0, dimensions.innerWidth]
            y_axis_func = d3.axisLeft
        } else {
            width_range = [dimensions.innerWidth, 0]
            y_axis_func = d3.axisRight
            y_axis_transform = dimensions.innerWidth
        }
    }

    //get label-axis positions and labels
    let label_axis_pos = dendrogram.axis_labels.map((x) => x.x)
    let label_axis_label = dendrogram.axis_labels.map((x) => x.label)

    //create X-axis
    var xScale = d3.scaleLinear()        
    xScale.domain(width_limits).range(width_range)
    var xAxis = x_axis_func(xScale)  
    xAxis.tickValues(label_axis_pos)
    xAxis.tickFormat((d,i) => label_axis_label[i]);


    //create y-axis
    var yScale = d3.scaleLinear()
    yScale.domain(height_limits).range(height_range)
    var yAxis = y_axis_func(yScale)


    //add X-axis to plot
    let xg = plot.append("g")
        .attr("id", "x-axis")            
        .attr("transform", "translate(0," + x_axis_transform + ")")
        .call(xAxis)

    //add Y-axis to plot
    let yg = plot.append("g")
    .attr("id", "y-axis")            
    .attr("transform", "translate(" + y_axis_transform + ",0)")
    .call(yAxis)
}

/* // Add a click handler to our button. It will send data back to Streamlit.
let numClicks = 0

button.onclick = function(): void {
  // Increment numClicks, and pass the new value back to
  // Streamlit via `Streamlit.setComponentValue`.
  numClicks += 1
  Streamlit.setComponentValue(numClicks)
} */


/**
 * The component's render function. This will be called immediately after
 * the component is initially loaded, and then again every time the
 * component gets new data from Python.
 */
function onRender(event: Event): void {
    // Get the RenderData from the event
    const data = (event as CustomEvent<RenderData>).detail

    var dendrogram: Dendrogram = data.args['data']
    let margin: Margin = { top: 20, right: 10, bottom: 20, left: 10 }    
    let dimensions: Dimensions = {
        height:  data.args['height'],
        width: data.args['width'],
        margin: margin,
        innerHeight: 0,
        innerWidth: 0,
        orientation: data.args['orientation']
    }    
    if (dimensions.orientation === Orientation.right) {
        dimensions.margin.right = 50
    } else {
        dimensions.margin.left = 50
    }

    dimensions.innerHeight = dimensions.height - dimensions.margin.top - dimensions.margin.bottom
    dimensions.innerWidth = dimensions.width - dimensions.margin.left - dimensions.margin.right    

    let plot = create_container(dimensions)
    create_axis(plot, dimensions, dendrogram)

    Streamlit.setFrameHeight()
}

// Attach our `onRender` handler to Streamlit's render event.
Streamlit.events.addEventListener(Streamlit.RENDER_EVENT, onRender)

// Tell Streamlit we're ready to start receiving data. We won't get our
// first RENDER_EVENT until we call this function.
Streamlit.setComponentReady()

// Finally, tell Streamlit to update our initial height. We omit the
// `height` parameter here to have it default to our scrollHeight.
Streamlit.setFrameHeight()
