import { Streamlit, RenderData } from "streamlit-component-lib"
import * as d3 from 'd3'

interface AxisLabel {
    x: number
    label: string
    labelsize: number
}

interface ClusterLink {
    x: number[]
    y: number[]
    fillcolor: string
    size: number
}

interface ClusterNode {
    x: number
    y: number
    edgecolor: string
    fillcolor: string
    label: string
    hovertext: Object[] | string
    size: number
    labelsize: number
    labelcolor: string

}

interface Dendrogram {
    axis_labels: AxisLabel[]
    links: ClusterLink[]
    nodes: ClusterNode[]
}

interface Margin {
    top: number
    right: number
    bottom: number
    left: number
}

function create_container(margin: Margin, width: number, height: number): d3.Selection<SVGGElement, unknown, HTMLElement, any> {

    outerWidth = width,
        outerHeight = height,
        innerWidth = outerWidth - margin.left - margin.right,
        innerHeight = outerHeight - margin.top - margin.bottom

    // append svg element to the body of the page
    // set dimensions and position of the svg element
    let svg = d3
        .select("body")
        .append("svg")
        .attr("id", "idendro")
        .attr("width", width)
        .attr("height", height)

    let plot = svg.append("g")
        .attr("transform", "translate(" + margin.left + "," + margin.top + ")")
        .attr("id", "idendro-container");

    return plot
}

/* // Add a click handler to our button. It will send data back to Streamlit.
let numClicks = 0

button.onclick = function(): void {
  // Increment numClicks, and pass the new value back to
  // Streamlit via `Streamlit.setComponentValue`.
  numClicks += 1
  Streamlit.setComponentValue(numClicks)
} */


/**
 * The component's render function. This will be called immediately after
 * the component is initially loaded, and then again every time the
 * component gets new data from Python.
 */
function onRender(event: Event): void {
    // Get the RenderData from the event
    const data = (event as CustomEvent<RenderData>).detail

    var dendrogram: Dendrogram = data.args['data']
    let margin: Margin = { top: 20, right: 10, bottom: 20, left: 10 }
    let height = data.args['height']
    let width = data.args['width']
    console.log(dendrogram)
    console.log(data.args)

    Streamlit.setFrameHeight()
}

// Attach our `onRender` handler to Streamlit's render event.
Streamlit.events.addEventListener(Streamlit.RENDER_EVENT, onRender)

// Tell Streamlit we're ready to start receiving data. We won't get our
// first RENDER_EVENT until we call this function.
Streamlit.setComponentReady()

// Finally, tell Streamlit to update our initial height. We omit the
// `height` parameter here to have it default to our scrollHeight.
Streamlit.setFrameHeight()
